-- business questions

-- Q1. what is the total revenue generated by males vs female customers?
select gender, sum(purchase_amount) as revenue
from customer
group by gender

-- Q2. which customer used a discount but still spent more than the average purchase amount?


SELECT customer_id, purchase_amount, discount_applied
FROM customer
WHERE discount_applied = 'Yes'
  AND purchase_amount >= (
    SELECT AVG(purchase_amount)
    FROM customer
    WHERE discount_applied = 'Yes'
  )
ORDER BY customer_id;

--Q3. Which are the top 5 products with the highest average review rating?

SELECT item_purchased, ROUND(AVG(review_rating::numeric),2) AS avg_rating
FROM customer
GROUP BY item_purchased
ORDER BY avg_rating DESC
LIMIT 5;

--Q4. Compare the average purhase amounts between standard and express shipping.

select shipping_type, ROUND(avg(purchase_amount),2) as avg_purchase_amount
from customer
where shipping_type in ('Standard','Express')
group by shipping_type
order by avg_purchase_amount

--Q5. Do subscribed customers spend more? compare average and total revenue
--between subscribers and non-subscribers.


select  subscription_status,COUNT(customer_id), ROUND(avg(purchase_amount),2) as avg_purchase_amount, sum(purchase_amount) as total_revenue
from customer
group by subscription_status
order by avg_purchase_amount

--Q6. Which 5 products have the highest percentage of purchase with discount applied?


select item_purchased,
ROUND(100 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*),2) as discount_rate
from customer
group by item_purchased
order by discount_rate desc
limit 5;

--Q7. Segment Customers Into new, Returning, and Loyal based on their total
--number of previous purchases, and show the count of each segment.

-- used cte here to solve the question
with customer_type as (
select customer_id, previous_purchases,
case 
	when previous_purchases = 1 then 'New'
	when previous_purchases between 2 and 10 then 'Returning'
	else 'loyal'
	end as customer_segment
from customer
)
select customer_segment, count(*) as "Number of Customers"
from customer_type
group by customer_segment;
)

--Q8. what are the top 3 most purchased products within each category?

with item_counts as (
select category,
item_purchased,
count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc) as item_rank
from customer
group by category, item_purchased
)

select item_rank, category, item_purchased, total_orders
from item_counts
where item_rank <= 3

--Q9. are customers who are repeat buyers (more than 5 purchases) also likely to subscribe?

select subscription_status,
count(customer_id) as repeat_buyers
from customer
where previous_purchases > 5
group by subscription_status;

--Q10. what is the revenue contribution of each age group?

select age_group,
sum(purchase_amount) as total_revenue
from customer
group by age_group
order by total_revenue desc;